var target=document.getElementById("contact"),scrollToTopBtn=document.querySelector(".scroll-btn"),rootElement=document.documentElement;function callback(o,e){o.forEach(o=>{o.isIntersecting?scrollToTopBtn.classList.add("show-btn"):scrollToTopBtn.classList.remove("show-btn")})}function scrollToTop(){rootElement.scrollTo({top:0,behavior:"smooth"})}scrollToTopBtn.addEventListener("click",scrollToTop);let observer=new IntersectionObserver(callback);observer.observe(target);